package com.bestjoy.app.alipay;import android.text.TextUtils;import java.util.Map;public class PayResult {	public String resultStatus;	public String result;	public String memo;	/**针对pay2版本*/	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	/**针对pay版本*/	public PayResult(String rawResult) {		try {			String[] resultParams = rawResult.split(";");			for (String resultParam : resultParams) {				if (resultParam.startsWith("resultStatus")) {					resultStatus = gatValue(resultParam, "resultStatus");				}				if (resultParam.startsWith("result")) {					result = gatValue(resultParam, "result");				}				if (resultParam.startsWith("memo")) {					memo = gatValue(resultParam, "memo");				}			}		} catch (Exception e) {			e.printStackTrace();		}	}	private String gatValue(String content, String key) {		String prefix = key + "={";		return content.substring(content.indexOf(prefix) + prefix.length(),				content.lastIndexOf("}"));	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}}